import React, { useState } from "react";

const Home = () => {
  const [userInfo, setUserInfo] = useState({});
  const [germanGrade, setGermanGrade] = useState(0);
  const [gradeFound, setGradeFound] = useState(false);
  function handleBlur(e) {
    userInfo[e.target.name] = e.target.value;
    setUserInfo(userInfo);
    console.log(userInfo);
  }
  function handleClick(e) {
    e.preventDefault();
    const userGrade =
      ((userInfo.maxGrade - userInfo.userGrade) /
        (userInfo.maxGrade - userInfo.minGrade)) *
        3 +
      1;
    setGermanGrade(userGrade);

    setGradeFound(true);
  }

  return (
    <div
      style={{
        backgroundColor: "#ddd",
        borderRadius: "8px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        height: "150vh",
      }}
    >
      <form>
        <div>
          <h1 style={{ paddingBottom: "15px" }}>Breast Cancer Prediction</h1>
        </div>
        {[
          "Radius Mean",
          "Texture Mean",
          "Perimeter Mean",
          "Area Mean",
          "Smoothness Mean",
          "Compactness Mean",
          "Concavity Mean",
          "Concave Points Mean",
          "Symmetry Mean",
          "Fractal Dimension Mean",
          "Radius Se",
          "Texture Se",
          "Perimeter Se",
          "Area Se",
          "Smoothness Se",
          "Compactness Se",
          "Concavity Se",
          "Concave Points Se",
          "Symmetry Se",
          "Fractal Dimension Se",
          "Radius Worst",
          "Texture Worst",
          "Perimeter Worst",
          "Area Worst",
          "Smoothness Worst",
          "Compactness Worst",
          "Concavity Worst",
          "Concave Points Worst",
          "Symmetry Worst",
          "Fractal Dimension Worst",
        ].map((label, index) => (
          <div
            key={index}
            style={{
              marginBottom: "10px",
            }}
          >
            <label
              htmlFor={`input${index}`}
              style={{
                paddingRight: "10px",
              }}
            >
              {label}
            </label>
            <input
              id={`input${index}`}
              style={{
                fontSize: "18px",
                borderRadius: "4px",
                border: "1px solid #ccc",
              }}
              onBlur={handleBlur}
              type="text"
            />
          </div>
        ))}
        <div style={{ marginTop: "20px" }}>
          <button
            onClick={handleClick}
            style={{
              padding:
                "10px 10px" /* Adds padding for a larger clickable area */,
              backgroundColor: "#007BFF" /* Sets a blue background color */,
              color: "#fff" /* Sets text color to white */,
              border: "none" /* Removes the default border */,
              borderRadius: "5px" /* Rounds the corners */,
              fontSize: "16px" /* Adjusts the font size */,
              cursor: "pointer" /* Changes the cursor to a pointer on hover */,
              boxShadow:
                "0px 4px 6px rgba(0, 0, 0, 0.1)" /* Adds a subtle shadow */,
              transition:
                "background-color 0.3s, transform 0.3s" /* Smooth transition for hover effects */,
            }}
            onMouseOver={(e) =>
              (e.currentTarget.style.backgroundColor = "#0056b3")
            } /* Darker background on hover */
            onMouseOut={(e) =>
              (e.currentTarget.style.backgroundColor = "#007BFF")
            } /* Reverts background color when not hovering */
          >
            Predict
          </button>
        </div>

        {gradeFound && <p>Your German Grade is {germanGrade}</p>}
      </form>
    </div>
  );
};

export default Home;
