import React, { useState } from "react";

const Home = () => {
  const [userInfo, setUserInfo] = useState({});
  const [germanGrade, setGermanGrade] = useState(0);
  const [gradeFound, setGradeFound] = useState(false);
  function handleBlur(e) {
    userInfo[e.target.name] = e.target.value;
    setUserInfo(userInfo);
    console.log(userInfo);
  }
  function handleClick(e) {
    e.preventDefault();
    const userGrade =
      ((userInfo.maxGrade - userInfo.userGrade) /
        (userInfo.maxGrade - userInfo.minGrade)) *
        3 +
      1;
    setGermanGrade(userGrade);

    setGradeFound(true);
  }

  return (
    <div style={{ height: "100vh" }}>
      <div>
        <h3 style={{ textAlign: "center" }}>Breast Cancer Prediction</h3>
      </div>
      <div style={{ backgroundColor: "#ddd" }}>
        <form>
          <div
            style={{ display: "flex", justifyContent: "center", gap: "10px", marginTop: "10px"}}
          >
            <label htmlFor="userInput">Radius Mean</label>
            <input onBlur={handleBlur} type="text"></input>
          </div>
          <button
            onClick={handleClick}
            type="submit"
            className="btn btn-primary"
          >
            Convert
          </button>
          {gradeFound && <p>Your German Grade is {germanGrade}</p>}
        </form>
      </div>
    </div>
  );
};

export default Home;
