import React, { useState } from "react";

const Home = () => {
  const [userInfo, setUserInfo] = useState({});
  const [germanGrade, setGermanGrade] = useState(0);
  const [gradeFound, setGradeFound] = useState(false);
  function handleBlur(e) {
    userInfo[e.target.name] = e.target.value;
    setUserInfo(userInfo);
    console.log(userInfo);
  }
  function handleClick(e) {
    e.preventDefault();
    const userGrade =
      ((userInfo.maxGrade - userInfo.userGrade) /
        (userInfo.maxGrade - userInfo.minGrade)) *
        3 +
      1;
    setGermanGrade(userGrade);

    setGradeFound(true);
  }

  return (
    <body style={{ backgroundColor: "#ddd"}}>
       <h3 style={{ textAlign: "center", paddingTop: "10px" }}>
        German Grade Calculator for Bangladeshi Students
      </h3>
      <div className="row" style={{ backgroundColor: "#ddd" }}>
        <div className="col-md-6">
          <div className="ml-5 mr-5 mt-3">
            <form>
              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Nmax</label>
                <input
                  onBlur={handleBlur}
                  name="maxGrade"
                  type="text"
                  className="form-control"
                  id="exampleInputPassword1"
                ></input>
                <small id="emailHelp" className="form-text text-muted">
                  Best possible grade in your grading system
                </small>
              </div>

              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Nmin</label>
                <input
                  onBlur={handleBlur}
                  name="minGrade"
                  type="text"
                  className="form-control"
                  id="exampleInputPassword1"
                ></input>
                <small id="emailHelp" className="form-text text-muted">
                  Minimum grade to get the B.Sc. degree
                </small>
              </div>

              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Your Score</label>
                <input
                  onBlur={handleBlur}
                  name="userGrade"
                  type="text"
                  className="form-control"
                  id="exampleInputPassword1"
                ></input>
                <small id="emailHelp" className="form-text text-muted">
                  Your final grade
                </small>
              </div>

              <button
                onClick={handleClick}
                type="submit"
                className="btn btn-primary"
              >
                Convert
              </button>
              {gradeFound && <p>Your German Grade is {germanGrade}</p>}
            </form>
          </div>
        </div>
      </div>
    </body>
  );
};

export default Home;
