import React, { useState } from "react";

const Home = () => {
  const [userInfo, setUserInfo] = useState({});
  const [germanGrade, setGermanGrade] = useState(0);
  const [gradeFound, setGradeFound] = useState(false);
  function handleBlur(e) {
    userInfo[e.target.name] = e.target.value;
    setUserInfo(userInfo);
    console.log(userInfo);
  }
  function handleClick(e) {
    e.preventDefault();
    const userGrade =
      ((userInfo.maxGrade - userInfo.userGrade) /
        (userInfo.maxGrade - userInfo.minGrade)) *
        3 +
      1;
    setGermanGrade(userGrade);

    setGradeFound(true);
  }

  return (
    <div style={{height: "100vh"}}>
       <h3 style={{ textAlign: "center"}}>
        Breast Cancer Prediction
      </h3>
      <div style={{ backgroundColor: "#ddd"}}>
            <form>
              <div style={{display: "flex", justifyContent: "center", gap: "10px"}}>
                <label htmlFor="userInput">Radius Mean  </label>
                <input
                  onBlur={handleBlur}
                  type="text"
                ></input>
              </div>

              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Nmin</label>
                <input
                  onBlur={handleBlur}
                  name="minGrade"
                  type="text"
                  className="form-control"
                  id="exampleInputPassword1"
                ></input>
                <small id="emailHelp" className="form-text text-muted">
                  Minimum grade to get the B.Sc. degree
                </small>
              </div>

              <div className="form-group">
                <label htmlFor="exampleInputPassword1">Your Score</label>
                <input
                  onBlur={handleBlur}
                  name="userGrade"
                  type="text"
                  className="form-control"
                  id="exampleInputPassword1"
                ></input>
                <small id="emailHelp" className="form-text text-muted">
                  Your final grade
                </small>
              </div>

              <button
                onClick={handleClick}
                type="submit"
                className="btn btn-primary"
              >
                Convert
              </button>
              {gradeFound && <p>Your German Grade is {germanGrade}</p>}
            </form>
          </div>
        </div>
  );
};

export default Home;
