import React, { useState } from "react";

const Home = () => {
  const [userInfo, setUserInfo] = useState({});
  const [germanGrade, setGermanGrade] = useState(0);
  const [gradeFound, setGradeFound] = useState(false);
  function handleBlur(e) {
    userInfo[e.target.name] = e.target.value;
    setUserInfo(userInfo);
    console.log(userInfo);
  }
  function handleClick(e) {
    e.preventDefault();
    const userGrade =
      ((userInfo.maxGrade - userInfo.userGrade) /
        (userInfo.maxGrade - userInfo.minGrade)) *
        3 +
      1;
    setGermanGrade(userGrade);

    setGradeFound(true);
  }

  return (
    <div>
      <div>
        <h3 style={{textAlign: "center"}}>Breast Cancer Prediction</h3>
      </div>

      <div
        style={{
          backgroundColor: "#ddd",
          borderRadius: "8px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <form>
          {[
            "Radius Mean",
            "Texture Mean",
            "Perimeter Mean",
            "Area Mean",
            "Smoothness Mean",
            "Compactness Mean",
            "Concavity Mean",
            "Concave Points Mean",
            "Symmetry Mean",
            "Fractal Dimension Mean",
            "Radius Se",
            "Texture Se",
            "Perimeter Se",
            "Area Se",
            "Smoothness Se",
            "Compactness Se",
            "Concavity Se",
            "Concave Points Se",
            "Symmetry Se",
            "Fractal Dimension Se",
            "Radius Worst",
            "Texture Worst",
            "Perimeter Worst",
            "Area Worst",
            "Smoothness Worst",
            "Compactness Worst",
            "Concavity Worst",
            "Concave Points Worst",
            "Symmetry Worst",
            "Fractal Dimension Worst",
          ].map((label, index) => (
            <div
              key={index}
              style={{
                marginBottom: "10px",
              }}
            >
              <label
                htmlFor={`input${index}`}
                style={{
                  paddingRight: "10px",
                }}
              >
                {label}
              </label>
              <input
                id={`input${index}`}
                style={{
                  fontSize: "18px",
                  borderRadius: "4px",
                  border: "1px solid #ccc",
                }}
                onBlur={handleBlur}
                type="text"
              />
            </div>
          ))}
          <div style={{ marginTop: "20px" }}>
            <button
              onClick={handleClick}
              type="submit"
              className="btn btn-primary"
            >
              Convert
            </button>
          </div>
          {gradeFound && <p>Your German Grade is {germanGrade}</p>}
        </form>
      </div>
    </div>
  );
};

export default Home;
